import React from "react";
import "./Slotstable.css";

export default function CourtSchedule (){

  const [selectedDate, setSelectedDate] = useState("2024-02-27");

  const handleDateChange = (e) => {
    setSelectedDate(e.target.value);
  };

  return (
   
   <div className="court-schedule">
       <div className="date-selector">
          <input
            type="date"
            value={selectedDate}
            onChange={handleDateChange}
            className="calendar-input"
          />
        </div>

      <div className="header">
        <div className="header-item">Time</div>
        {["Court 1", "Court 2", "Court 3", "Court 4", "Court 5", "Court 6"].map((court, index) => (
          <div className="header-item" key={index}>{court}</div>
        ))}
      </div>
      <div className="body">
        {data.map((row, rowIndex) => (
          <div className="row" key={rowIndex}>
            <div className="time">{row.time}</div>
            {row.courts.map((court, courtIndex) => (
              <div
                className={`court-cell ${court.type || ""}`}
                key={courtIndex}
              >
                {court.name && <p className="name">{court.name}</p>}
                {court.items && <p className="items">{court.items}</p>}
                {court.price && <p className="price">{court.price}</p>}
                {court.count && <p className="count">{court.count}</p>}
                {court.coach && <p className="coach">{court.coach}</p>}
              </div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
};
