import React, { useEffect, useState } from "react";
import NavbarUser from "../components/NavbarUser";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

export default function Bookslots() {
  const { bookingId } = useParams(); 
  const navigate = useNavigate(); 
  const [bookingData, setBookingData] = useState(null); 
  const [formData, setFormData] = useState({}); 

  useEffect(() => {
    const fetchBooking = async () => {
      try {
        const response = await axios.get(
          `${process.env.REACT_APP_BACKENDAPI}/api/booking/${bookingId}`
        );
        setBookingData(response.data);
        setFormData({
          courtName: response.data.courtId.name || "",
          bookingSlot: response.data.bookingSlot || "",
          bookingStatus: response.data.bookingStatus || "",
          user: "",
        });
      } catch (error) {
        console.error("Error fetching booking data:", error);
      }
    };

    fetchBooking();
  }, [bookingId]);

  // Handle form input changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const updatedData = {
        bookingStatus: "booked",
        user: formData.user, // User's input data
      };
      await axios.put(
        `${process.env.REACT_APP_BACKENDAPI}/api/booking/book/${bookingId}`,
        updatedData
      );
      alert("Booking updated successfully!");
      navigate("/"); // Redirect after successful update
    } catch (error) {
      console.error("Error updating booking:", error);
      alert("Failed to update booking. Please try again.");
    }
  };

  if (!bookingData) return <div>Loading...</div>;

  return (
    <div>
      <NavbarUser />
      <div className="form-container">
        <h2>Book Slot</h2>
        <form onSubmit={handleSubmit}>
          {/* Display court name */}
          <div className="form-group">
            <label>Court Name:</label>
            <input
              type="text"
              value={formData.courtName}
              disabled
              className="form-control"
            />
          </div>

          {/* Display booking slot */}
          <div className="form-group">
            <label>Booking Slot:</label>
            <input
              type="text"
              value={formData.bookingSlot}
              disabled
              className="form-control"
            />
          </div>

          {/* Booking status */}
          <div className="form-group">
            <label>Booking Status:</label>
            <input
              type="text"
              value={formData.bookingStatus}
              disabled
              className="form-control"
            />
          </div>

          {/* User Name (for booking update) */}
          <div className="form-group">
            <label>Enter Your Name:</label>
            <input
              type="text"
              name="user"
              value={formData.user}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>

          {/* Submit button */}
          <div className="form-group">
            <button type="submit" className="btn btn-primary">
              Confirm Booking
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
