import React, { useEffect } from "react";
import "./Slotstable.css";
import { useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

export default function CourtSchedule (){

  const today = new Date();
  const formattedToday = today.toISOString().split("T")[0];
  const [selectedDate, setSelectedDate] = useState(formattedToday);
  const [courts , setCourts] = useState({});

  const { centreId } = useParams();

  const handleDateChange = (e) => {
    setSelectedDate(e.target.value);
  };

  useEffect(() => {
    const fetchCourts = async () => {
        const response = await axios.get(`${process.env.REACT_APP_BACKENDAPI}/api/court/center/${centreId}`);
        setCourts(response.data);
    };
    fetchCourts();
  }, []);

  const times = ['5:00 AM - 6:00 AM','6:00 AM - 7:00 AM', '7:00 AM - 8:00 AM', '8:00 AM - 9:00 AM', '9:00 AM - 10:00 AM', '10:00 AM - 11:00 AM', 
    '11:00 AM - 12:00 PM', '12:00 PM - 1:00 PM', '1:00 PM - 2:00 PM', '2:00 PM - 3:00 PM', '3:00 PM - 4:00 PM',
    '4:00 PM - 5:00 PM', '5:00 PM - 6:00 PM', '6:00 PM - 7:00 PM', '7:00 PM - 8:00 PM', '8:00 PM - 9:00 PM', '9:00 PM - 10:00 PM', '10:00 PM - 11:00 PM', '11:00 PM - 12:00 AM'];


  return (
    <div className="court-schedule">
        <div className="date-selector">
            <input
              type="date"
              value={selectedDate}
              onChange={handleDateChange}
              className="calendar-input"
            />
          </div>
          <div className="schedule-table">
      <table>
        <thead>
          <tr>
            <th>Court / Time</th>
            {times.map((time, index) => (
              <th key={index}>{time}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {courts.map((court, index) => (
            <tr key={index}>
              <td className="court-name">{court.name}</td>
              {times.map((time, i) => {
                const slot = court.slots.find((slot) => slot.time === time);
                return (
                  <td key={i} className={slot ? "filled-slot" : "empty-slot"}>
                    {slot ? slot.event : ""}
                  </td>
                );
              })}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
    </div>
  );
};
