import React, { useEffect, useState } from "react";
import NavbarUser from "../components/NavbarUser";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

export default function Bookslots() {
  const { bookingId } = useParams(); // Get bookingId from the URL
  const navigate = useNavigate(); // Redirect after successful booking
  const [bookingData, setBookingData] = useState(null); // Booking details
  const [formData, setFormData] = useState({}); // Form data
  const [userId, setUserId] = useState(""); // Logged-in user ID (replace this with actual user management logic)

  // Fetch booking data on component mount
  useEffect(() => {
    const fetchBooking = async () => {
      try {
        const response = await axios.get(
          `${process.env.REACT_APP_BACKENDAPI}/api/booking/${bookingId}`
        );
        setBookingData(response.data);
        setFormData({
          courtName: response.data.courtId.name,
          bookingSlot: response.data.bookingSlot,
          bookingStatus: response.data.bookingStatus,
        });
      } catch (error) {
        console.error("Error fetching booking data:", error);
      }
    };

    fetchBooking();
  }, [bookingId]);

  // Handle input changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Submit booking request
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.put(
        `${process.env.REACT_APP_BACKENDAPI}/api/booking/book/${bookingId}`,
        {
          userId, // Pass the logged-in user ID
          customername: formData.customerName, // Pass customer's name
        },
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } } // Add auth token if needed
      );

      alert(response.data.message); // Show success message
      navigate("/"); // Redirect to homepage or another page
    } catch (error) {
      console.error("Error updating booking:", error);
      alert(
        error.response?.data?.message || "An error occurred. Please try again."
      );
    }
  };

  if (!bookingData) return <div>Loading...</div>;

  return (
    <div>
      <NavbarUser />
      <div className="form-container">
        <h2>Book Slot</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Court Name:</label>
            <input
              type="text"
              value={formData.courtName}
              disabled
              className="form-control"
            />
          </div>
          <div className="form-group">
            <label>Booking Slot:</label>
            <input
              type="text"
              value={formData.bookingSlot}
              disabled
              className="form-control"
            />
          </div>
          <div className="form-group">
            <label>Booking Status:</label>
            <input
              type="text"
              value={formData.bookingStatus}
              disabled
              className="form-control"
            />
          </div>
          <div className="form-group">
            <label>Customer Name:</label>
            <input
              type="text"
              name="customerName"
              value={formData.customerName || ""}
              onChange={handleChange}
              required
              className="form-control"
            />
          </div>
          <div className="form-group">
            <button type="submit" className="btn btn-primary">
              Confirm Booking
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
