import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Allcentres.css"; // Add CSS for styling

const CentresList = () => {
  const [centres, setCentres] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch centres data from API
    const fetchCentres = async () => {
      try {
        const response = await axios.get("/api/centre");
        setCentres(response.data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchCentres();
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="card-container">
      {centres.map((centre) => (
        <div className="card" key={centre._id}>
          <h3 className="card-title">{centre.name}</h3>
          <p className="card-info">Location: {centre.location}</p>
          <p className="card-info">City: {centre.city}</p>
          <p className="card-info">Pincode: {centre.pincode}</p>
        </div>
      ))}
    </div>
  );
};

export default CentresList;
