import React, { useState } from "react";
import "./Sportsbar.css";

export default function Sportsbar() {
    const [selectedDate, setSelectedDate] = useState("2024-02-27");
    const [activeSport, setActiveSport] = useState("Swimming");
  
    const sports = ["Swimming", "Badminton"];
  
    const handleDateChange = (e) => {
      setSelectedDate(e.target.value);
    };
  
    return (
      <div className="header-filter">
        {/* Date Selector */}
        <div className="date-selector">
          <input
            type="date"
            value={selectedDate}
            onChange={handleDateChange}
            className="calendar-input"
          />
        </div>
  
        {/* Sports Category */}
        <div className="sports-category">
          {sports.map((sport, index) => (
            <button
              key={index}
              className={`sport-button ${activeSport === sport ? "active" : ""}`}
              onClick={() => setActiveSport(sport)}
            >
              {sport}
            </button>
          ))}
        </div>
      </div>
  );
};
