import React from "react";
import "./Slotstable.css";
import { useState } from "react";

export default function CourtSchedule (){

  const [selectedDate, setSelectedDate] = useState("2024-02-27");

  const handleDateChange = (e) => {
    setSelectedDate(e.target.value);
  };

  const times = ['5:00 AM - 6:00 AM','6:00 AM - 7:00 AM', '7:00 AM - 8:00 AM', '8:00 AM - 9:00 AM', '9:00 AM - 10:00 AM', '10:00 AM - 11:00 AM', 
    '11:00 AM - 12:00 PM', '12:00 PM - 1:00 PM', '1:00 PM - 2:00 PM', '2:00 PM - 3:00 PM', '3:00 PM - 4:00 PM',
    '4:00 PM - 5:00 PM', '5:00 PM - 6:00 PM', '6:00 PM - 7:00 PM', '7:00 PM - 8:00 PM', '8:00 PM - 9:00 PM', '9:00 PM - 10:00 PM', '10:00 PM - 11:00 PM', '11:00 PM - 12:00 AM'];
  
  const courts = [
    {
      name: "Court 1",
      slots: [
        { time: "4 AM", event: "Abbas" },
        { time: "5 AM", event: "Shantanu" },
        { time: "8 AM", event: "Coaching (Sourav Kumar)" },
      ],
    },
    {
      name: "Court 2",
      slots: [
        { time: "4 AM", event: "Vinay Hasya" },
        { time: "5 AM", event: "Kailash (₹300)" },
        { time: "8 AM", event: "Coaching (Sourav Kumar)" },
      ],
    },
    {
      name: "Court 3",
      slots: [
        { time: "5 AM", event: "Venkatesh (Collect)" },
        { time: "6 AM", event: "Blocked" },
        { time: "8 AM", event: "Coaching (Puja Minz)" },
      ],
    },
    // Add more courts as needed
  ];


  return (
    <div className="court-schedule">
        <div className="date-selector">
            <input
              type="date"
              value={selectedDate}
              onChange={handleDateChange}
              className="calendar-input"
            />
          </div>
          <table className="schedule-table">
        <thead>
          <tr>
            <th className="fixed-column">Court / Time</th>
            {times.map((time, index) => (
              <th key={index}>{time}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {courts.map((court, index) => (
            <tr key={index}>
              <td className="court-name fixed-column">{court.name}</td>
              {times.map((time, i) => {
                const slot = court.slots.find((slot) => slot.time === time);
                return (
                  <td key={i} className={slot ? "filled-slot" : "empty-slot"}>
                    {slot ? slot.event : ""}
                  </td>
                );
              })}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};
