import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import { AuthContext } from "../context/AuthContext"; 

export default function UserBookedslot() {
  const [userBookings, setUserBookings] = useState([]); 
  const backendAPI = process.env.REACT_APP_BACKENDAPI; 
  const context = useContext(AuthContext); 
  const userId = context?.user?.user?.id; 

 
  useEffect(() => {
    const fetchUserBooking = async () => {
      try {
        if (userId) {
          const res = await axios.get(
            `${backendAPI}/api/booking/user/${userId}`
          );
          setUserBookings(res.data); 
        } else {
          console.error("No user ID found in context.");
        }
      } catch (error) {
        console.error("Error fetching user bookings:", error);
      }
    };

    fetchUserBooking();
  }, [userId, backendAPI]);

  return (
    <div className="user-booked-container">
      <h2>Your Booked Slots</h2>
      {userBookings.length > 0 ? (
        <div className="booking-cards">
          {userBookings.map((booking) => (
            <div className="booking-card" key={booking._id}>
              <h3>Center: {booking.centerId?.name}</h3>
              <p><strong>Court:</strong> {booking.courtId?.name}</p>
              <p><strong>Sport:</strong> {booking.sportId?.name}</p>
              <p><strong>Date:</strong> {new Date(booking.date).toLocaleDateString()}</p>
              <p><strong>Slot:</strong> {booking.bookingSlot}</p>
            </div>
          ))}
        </div>
      ) : (
        <p>No bookings found.</p>
      )}
    </div>
  );
}
