import React, { useState } from "react";
import "./Sportsbar.css";

export default function Sportsbar() {
  const [selectedDate, setSelectedDate] = useState("27th Feb 2024");
  const [activeSport, setActiveSport] = useState("Swimming");

  const dates = ["27th Feb 2024", "28th Feb 2024", "1st Mar 2024"];
  const sports = ["Swimming", "Badminton"];

  return (
    <div className="header-filter">
      {/* Date Selector */}
      <div className="date-selector">
        <select
          value={selectedDate}
          onChange={(e) => setSelectedDate(e.target.value)}
        >
          {dates.map((date, index) => (
            <option key={index} value={date}>
              {date}
            </option>
          ))}
        </select>
      </div>

      {/* Sports Category */}
      <div className="sports-category">
        {sports.map((sport, index) => (
          <button
            key={index}
            className={`sport-button ${activeSport === sport ? "active" : ""}`}
            onClick={() => setActiveSport(sport)}
          >
            {sport}
          </button>
        ))}
      </div>
    </div>
  );
};
