import CourtSchedule from "../components/CourtSchedule";
import NavbarUser from "../components/NavbarUser";
import Sportsbar from "../components/Sportsbar";
import "./CentreSchedule.css";

import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

export default function Viewslots() {

    const { centerId } = useParams();
    const [center, setCenter] = useState(null);
    const [courts, setCourts] = useState([]);
    const [bookings, setBookings] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");
  
    useEffect(() => {
      const fetchData = async () => {
        try {
          // Fetch center details by ID
          const centerRes = await axios.get(`${process.env.REACT_APP_BACKENDAPI}/center/${centerId}`);
          setCenter(centerRes.data);
  
          // Fetch courts by center ID
          const courtsRes = await axios.get(`${process.env.REACT_APP_BACKENDAPI}/court/center/${centerId}`);
          setCourts(courtsRes.data);
  
          // Fetch bookings by center ID
          const bookingsRes = await axios.get(`${process.env.REACT_APP_BACKEND_URL}/bookings?centerId=${centerId}`);
          setBookings(bookingsRes.data);
  
          setLoading(false);
        } catch (err) {
          setError(err.message);
          setLoading(false);
        }
      };
  
      fetchData();
    }, [centerId]);

    return (
        <div className="centreschedule">
        <NavbarUser/>
        <div>
        <div>
            <span>Schedule</span>
        </div>
        <Sportsbar/>
        <CourtSchedule/>

        </div>
        </div>
    )
}
