import "./Navbar.css";
import profilepic from "./profile.jpg";
import { AuthContext } from "../context/AuthContext";
import { useContext, useEffect, useState } from "react";
import axios from "axios";

export default function NavbarAdmin() {
  const context = useContext(AuthContext);
  const [centerInfo, setCenterInfo] = useState({ name: '', location: '' });

  useEffect(() => {
    const fetchCenterDetails = async () => {
      try {
        if (context.user?.centerId) {
          const response = await axios.get(
            `${process.env.REACT_APP_BACKENDAPI}/api/admin/centers/${context.user.centerId}`
          );
          setCenterInfo({
            name: response.data.name,
            location: response.data.location,
          });
        }
      } catch (error) {
        console.error("Error fetching center details:", error);
      }
    };

    fetchCenterDetails();
  }, [context.user?.centerId, context]);

  return (
    <div className="navbar">
      <div className="navbar-logo">Game Theory</div>
      <div className="navbar-menu">
        <div className="navbar-item">
          <span>Schedule</span>
        </div>
        <div className="navbar-item">
          <span>Create Bookings</span>
        </div>
      </div>
      <div className="navbar-footer">
        <div className="profile">
          <img src={profilepic} alt="Profile" />
          <div>
            <p className="profile-name">{context.user?.user?.name || 'Username'}</p>
            <p className="profile-role">Admin</p>
          </div>
        </div>
        <div className="location">
          {centerInfo?.location || 'Loading location...'}
        </div>
      </div>
    </div>
  );
}
