import React, { useEffect, useState } from "react";
import axios from "axios";
import NavbarAdmin from "../components/NavbarAdmin";
import "./CreateSlots.css";

export default function CreateSlots() {
  const [courts, setCourts] = useState([]); // List of courts from backend
  const [selectedDate, setSelectedDate] = useState(""); // Selected date state
  const [selectedSlots, setSelectedSlots] = useState([]); // Selected time slots by user

  // Fetch courts from backend on page load
  useEffect(() => {
    const fetchCourts = async () => {
      try {
        const response = await axios.get(
          `${process.env.REACT_APP_BACKENDAPI}/api/court`
        );
        setCourts(response.data);
      } catch (error) {
        console.error("Error fetching courts:", error);
      }
    };

    fetchCourts();
  }, []);

  const handleDateChange = (e) => {
    setSelectedDate(e.target.value);
  };

  const handleSlotChange = (e) => {
    const { value, checked } = e.target;

    if (checked) {
      setSelectedSlots([...selectedSlots, value]); 
    } else {
      setSelectedSlots(selectedSlots.filter((slot) => slot !== value));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!selectedDate) {
      alert("Please select a date.");
      return;
    }

    if (selectedSlots.length === 0) {
      alert("Please select at least one time slot.");
      return;
    }

    try {
      for (let slot of selectedSlots) {
        const response = await axios.post(
          `${process.env.REACT_APP_BACKENDAPI}/api/booking`,
          {
            date: selectedDate,
            bookingSlot: slot,
            courtId: courts[0]._id, 
          }
        );

        console.log("Booking created successfully", response.data);
      }

      alert("Slots successfully created.");
    } catch (error) {
      console.error("Error creating bookings", error);
      alert("An error occurred while booking slots.");
    }
  };

  return (
    <div>
      <NavbarAdmin />
      <div className="createslots-form-container">
        <h2>Create Booking Slots</h2>
        <form onSubmit={handleSubmit} className="createslots-form">

          <div className="form-group">
            <label htmlFor="date">Select Date:</label>
            <input
              type="date"
              id="date"
              value={selectedDate}
              onChange={handleDateChange}
              required
            />
          </div>

          <div className="form-group">
            <p>Select Time Slots:</p>
            {courts.length > 0 ? (
              courts[0].slots?.map((slot, index) => (
                <div key={index} className="slot-options">
                  <input
                    type="checkbox"
                    id={slot}
                    value={slot}
                    onChange={handleSlotChange}
                  />
                  <label htmlFor={slot}>{slot}</label>
                </div>
              ))
            ) : (
              <p>Loading available slots...</p>
            )}
          </div>

          {/* Submit */}
          <div className="form-actions">
            <button type="submit" className="submit-btn">
              Submit
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
